mapper.select:
        SELECT


--         级联表的字段
        ,changpian.changpian_name as changpianName
        ,changpian.changpian_photo as changpianPhoto
        ,changpian.changpian_types as changpianTypes
        ,changpian.changpian_kucun_number as changpianKucunNumber
        ,changpian.changpian_old_money as changpianOldMoney
        ,changpian.changpian_price as changpianPrice
        ,changpian.changpian_new_money as changpianNewMoney
        ,changpian.changpian_clicknum as changpianClicknum
        ,changpian.shangxia_types as shangxiaTypes
        ,changpian.changpian_delete as changpianDelete
        ,changpian.changpian_content as changpianContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM cart  a
        left JOIN changpian changpian ON a.changpian_id = changpian.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and (
                    a.changpian_id = #{params.changpianId}
                )


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.changpian_id IS NOT NULL


                and changpian.shangjia_id = #{params.shangjiaId}


                and changpian.changpian_name like CONCAT('%',#{params.changpianName},'%')


                and changpian.changpian_types = #{params.changpianTypes}



                  and changpian.changpian_kucun_number >= #{params.changpianKucunNumberStart}   


                  and changpian.changpian_kucun_number <= #{params.changpianKucunNumberEnd}   


                and changpian.changpian_kucun_number = #{params.changpianKucunNumber}


                  and changpian.changpian_old_money >= #{params.changpianOldMoneyStart}   


                  and changpian.changpian_old_money <= #{params.changpianOldMoneyEnd}   


                  and changpian.changpian_price >= #{params.changpianPriceStart}   


                  and changpian.changpian_price <= #{params.changpianPriceEnd}   


                and changpian.changpian_price = #{params.changpianPrice}


                  and changpian.changpian_new_money >= #{params.changpianNewMoneyStart}   


                  and changpian.changpian_new_money <= #{params.changpianNewMoneyEnd}   


                  and changpian.changpian_clicknum >= #{params.changpianClicknumStart}   


                  and changpian.changpian_clicknum <= #{params.changpianClicknumEnd}   


                and changpian.changpian_clicknum = #{params.changpianClicknum}


                and changpian.shangxia_types = #{params.shangxiaTypes}



                  and changpian.changpian_delete >= #{params.changpianDeleteStart}   


                  and changpian.changpian_delete <= #{params.changpianDeleteEnd}   


                and changpian.changpian_delete = #{params.changpianDelete}


                and changpian.changpian_content like CONCAT('%',#{params.changpianContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
